def load_page(page_num):
    paths = [
        f"LiberPrimus/pages/page_{page_num:02d}/runes.txt",
        f"LiberPrimus/pages/page_{page_num:02d}/runes.txt"
    ]
    for path in paths:
        if os.path.exists(path):
            with open(path, 'r', encoding='utf-8') as f:
                content = f.read()
            return [RUNE_TO_IDX[r] for r in content if r in RUNE_TO_IDX]
    return []

def decrypt(cipher, key):
    return [(c - k) % 29 for c, k in zip(cipher, key * (len(cipher) // len(key) + 1))]

def format_text(indices):
    return "".join([IDX_TO_LETTER[i] for i in indices]).lower()

def main():
    print("APPLYING MINED KEYS FOR PAGES 1-20")
    print("="*60)
    
    for page in range(1, 21):
        if page in KEYS:
            runes = load_page(page)
            if runes:
                plain = decrypt(runes, KEYS[page])
                text = format_text(plain)
                print(f"FULL TEXT PAGE {page}:")
                print(text)
                print("-" * 30)
            else:
                print(f"Could not load Page {page}")

if __name__ == "__main__":
    main()
